// this interface includes all the properties I expect to be present on items stored on a shelf
interface ShelfItem{
    title: string;
}

// Shelf< T extends ShelfItem > 
// this adds a constraint to the class
export default class Shelf<T extends ShelfItem>{

    private _items: Array<T> = new Array<T>();

    add(item: T): void{
        this._items.push(item);
    }

    getFirst(): T{
        return this._items[0];
    }

    find(title: string): T{
        return this._items.filter(item => item.title === title)[0];
    }

    printTitles(): void{
        this._items.forEach(item => console.log(item.title));
    }
}